const validators = (target,value) =>{
    let result = false;

    switch (target) {
        case "name":
            value.length<45?result=true:result=false;
            break;
        case "dni":
                if (!isNaN(value) && value.length<=8) result=true;
                else result =false
                break;     
        case "phone":
            if(value.length<9 ){
                if (!isNaN(value) && value.length<=9) result=true;
                else result =false
            }
            if (value.length===0)result=true
            break;      
        case "email":
            result = true

            break;          
        case "instructions":
            value.length<100?result=true:result=false;
            break;            
             

        default:
            break;
    }

    return result;
};

export{
    validators,
}
